<script>
  var far = false
  var mid = false
  var close = false
  function setfar() {
    far = true
    mid = false
    close = false
  }
  function setmid() {
    far = false
    mid = true
    close = false
  }
  function setclose() {
    far = false
    mid = false
    close = true
  }
</script>

